pragma solidity 0.8.15;

import '@openzeppelin/contracts/proxy/utils/UUPSUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol';
import '@openzeppelin/contracts/proxy/Clones.sol';
import '@openzeppelin/contracts/utils/Strings.sol';

import { AutomationRegistryInterface, State, Config } from '@chainlink/contracts/src/v0.8/interfaces/AutomationRegistryInterface1_2.sol';
import { LinkTokenInterface } from '@chainlink/contracts/src/v0.8/interfaces/LinkTokenInterface.sol';
import '@chainlink/contracts/src/v0.8/AutomationCompatible.sol';
import './interface/IKeeperRegistry.sol';
import './CampaignFactoryStorage.sol';
import './interface/ICampaignFactory.sol';
import './Consts.sol';

contract CampaignFactoryUpgradable is
  CampaignFactoryStorage,
  ICampaignFactory,
  UUPSUpgradeable,
  OwnableUpgradeable,
  AutomationCompatible
{
  function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

  function initialize(
    ICampaign campaign_,
    LinkTokenInterface link_,
    address registrar_,
    AutomationRegistryInterface registry_
  ) public initializer {
    i_campaign = campaign_;
    i_link = link_;
    registrar = registrar_;
    i_registry = registry_;
    __Ownable_init_unchained();
  }

  function modifyWhiteToken(IERC20Upgradeable token, uint256 amount) external onlyOwner {
    whiteTokens[token] = amount;
    emit EvWhiteTokenSet(token, amount);
  }

  function updateCampaignImplementation(ICampaign newImplementation) external onlyOwner {
    i_campaign = newImplementation;
    emit EvCampaignUpdated(newImplementation);
  }

  function createCampaign(
    IERC20Upgradeable token,
    uint256 amount,
    string memory name,
    string memory symbol,
    uint256 startTime,
    uint256 totalPeriod,
    uint256 periodLength,
    string calldata campaignUri,
    // please set to 0x
    bytes calldata zero
  ) public override returns (address campaign) {
    require(amount <= whiteTokens[token], 'CampaignF: amount exceed cap');
    require(block.timestamp < startTime, 'CampaignF: start too soon');
    require(i_link.balanceOf(address(this)) >= uint256(Consts.MIN_LINK_AMOUNT), 'CampaignF: not enough $Link');

    campaign = Clones.cloneDeterministic(
      address(i_campaign),
      keccak256(
        abi.encodePacked(
          Consts.SALT,
          msg.sender,
          token,
          amount,
          name,
          symbol,
          startTime,
          totalPeriod,
          periodLength,
          campaignUri
        )
      )
    );

    ICampaign(campaign).initialize(msg.sender, token, amount, name, symbol, startTime, totalPeriod, periodLength, campaignUri);

    // register chainLink keepUp
    _registerAndPredictID(
      string.concat('Sisyphus ', (Strings.toHexString(uint160(campaign), 20))),
      zero,
      address(campaign),
      Consts.UPKEEP_GAS_LIMIT,
      address(this),
      zero,
      Consts.MIN_LINK_AMOUNT,
      0
    );

    emit EvCampaignCreated(msg.sender, address(campaign));
  }

  function _registerAndPredictID(
    string memory name,
    bytes calldata encryptedEmail,
    address upkeepContract,
    uint32 gasLimit,
    address adminAddress,
    bytes calldata checkData,
    uint96 amount,
    uint8 source
  ) internal {
    (State memory state, Config memory _c, address[] memory _k) = i_registry.getState();
    uint256 oldNonce = state.nonce;
    bytes memory payload = abi.encode(
      name,
      encryptedEmail,
      upkeepContract,
      gasLimit,
      adminAddress,
      checkData,
      amount,
      source,
      address(this)
    );

    i_link.transferAndCall(registrar, amount, bytes.concat(Consts.registerSig, payload));

    (state, _c, _k) = i_registry.getState();
    uint256 newNonce = state.nonce;
    if (newNonce == oldNonce + 1) {
      uint256 upkeepID = uint256(
        keccak256(abi.encodePacked(blockhash(block.number - 1), address(i_registry), uint32(oldNonce)))
      );
      _saveUpKeep(upkeepContract, upkeepID);
    } else {
      revert('auto-approve disabled');
    }
  }

  /**
   * @dev check Upkeep
   */
  function checkUpkeep(bytes calldata) external view override returns (bool upkeepNeeded, bytes memory performData) {
    // travel the OnGoingCampaigns
    for (uint256 i = 0; i < OnGoingCampaigns.length; i++) {
      ICampaign campaign = ICampaign(OnGoingCampaigns[i]);
      (
        address _t,
        uint32 _e,
        bytes memory _c,
        uint96 balance,
        address _l,
        address _a,
        uint64 maxValid,
        uint96 _as
      ) = IKeeperRegistry(address(i_registry)).getUpkeep(keepUpRecords[address(campaign)].upKeepId);

      // check whether it's time to cancel
      bool canceled = maxValid != Consts.UINT64_MAX;
      if (campaign.status() == Consts.CampaignStatus.SETTLED && !canceled) {
        upkeepNeeded = true;
        performData = abi.encode(address(campaign), uint256(0));
        return (upkeepNeeded, performData);
      }

      // check whether it's time to withdraw after cancel
      if (block.number > maxValid + Consts.CANCELATION_DELAY && balance != uint96(0)) {
        upkeepNeeded = true;
        performData = abi.encode(address(campaign), uint256(1));
        return (upkeepNeeded, performData);
      }
    }
  }

  /**
   * @dev two kind of perform
   * 0: cancel upKeep of campaign
   * 1: withdraw upKeep $link
   */
  function performUpkeep(bytes calldata performData) external override {
    (address campaign, uint256 kind) = abi.decode(performData, (address, uint256));

    if (kind == 0) {
      // cancel keep up
      AutomationRegistryInterface(i_registry).cancelUpkeep(keepUpRecords[campaign].upKeepId);

      emit CampaignUpKeepCancelled(campaign);
    } else if (kind == 1) {
      // withdraw fund
      IKeeperRegistry(address(i_registry)).withdrawFunds(keepUpRecords[campaign].upKeepId, address(this));
      emit CampaignUpKeepWithdrawal(campaign);
    }
  }

  /**
   * @dev use in development
   */

  function cancelUpKeep(address campaign) external onlyOwner {
    AutomationRegistryInterface(i_registry).cancelUpkeep(keepUpRecords[campaign].upKeepId);
  }

  /**
   * @dev use in development
   */
  function withdrawUpKeep(address campaign) external onlyOwner {
    IKeeperRegistry(address(i_registry)).withdrawFunds(keepUpRecords[campaign].upKeepId, msg.sender);
  }

  /**
   * @dev use in development, manually edit data
   */
  function setKeepUpRecords(address campaign, UpKeepInfo calldata upKeepInfo) external onlyOwner {
    keepUpRecords[campaign] = upKeepInfo;
  }

  /**
   * @dev save upKeepId mapping, avoid stack too deep
   */
  function _saveUpKeep(address upkeepContract, uint256 upkeepID) private {
    // record up keep mapping
    keepUpRecords[upkeepContract].upKeepId = upkeepID;
    // just push, it doesn't matter too much not to pop.
    OnGoingCampaigns.push(upkeepContract);

    emit CampaignUpKeepRegistered(upkeepContract, upkeepID);
  }

  /**
   * @dev compatible with hardhat deploy, maybe removed later
   */
  function saveOwnerInAdmin() external {
    address o = owner();
    assembly {
      sstore(_ADMIN_SLOT, o)
    }
  }
}
","keccak256":"0x6729d3cd78e96747b2ad1c8106b98bad004e7e9759cd17039b4b4bad9e93e25f","license":"BUSL-1.1"},"contracts/Consts.sol":{"content":"//SPDX-License-Identifier: BUSL-1.1

pragma solidity 0.8.15;

interface KeeperRegistrarInterface {
  function register(
    string memory name,
    bytes calldata encryptedEmail,
    address upkeepContract,
    uint32 gasLimit,
    address adminAddress,
    bytes calldata checkData,
    uint96 amount,
    uint8 source,
    address sender
  ) external;
}

library Consts {
  //
  uint256 public constant DECIMAL = 10**6;
  uint256 public constant PROTOCOL_FEE = 10**5;
  uint256 public constant HOST_REWARD = 2 * 10**5;

  // challenge
  uint8 constant legalVoterRatio = 66;
  uint8 constant cheaterRatio = 30;
  uint8 constant challengerSuccessRatio = 60;
  uint8 constant successSharedRatio = 30;
  uint8 constant successProtocolRatio = 10;
  uint8 constant challengerFailRatio = 40;
  uint8 constant failSharedRatio = 75;
  uint8 constant failProtocolRatio = 25;
  uint8 constant SCALE = 100;

  bytes32 public constant SALT = keccak256(abi.encode('Sisyphus Protocol'));

  // tmp vitalik.eth
  address public constant PROTOCOL_RECIPIENT = 0xd8dA6BF26964aF9D7eEd9e03E53415D37aA96045;
  address public constant UPKEEP_ADMIN = 0x11F2241Bf12f1a640f78e5d1A0d3302D77fB5e78;

  // chainlink
  bytes4 public constant registerSig = KeeperRegistrarInterface.register.selector;
  // LinkSend
  uint96 public constant MIN_LINK_AMOUNT = 5000000000000000000;
  // upKeep GasLimit
  uint32 public constant UPKEEP_GAS_LIMIT = 5000000;

  // upKeep Cancel Delay same as the number in KeeperRegistry
  uint256 public constant CANCELATION_DELAY = 50;

  uint64 public constant UINT64_MAX = 2**64 - 1;

  enum CampaignType {
    IN_VALID,
    DAILY,
    WEEKLY
  }

  enum CampaignStatus {
    IN_VALID,
    NOT_START,
    ON_GOING,
    ENDED,
    SETTLED
  }
}
","keccak256":"0xd3bd83b65bda5446c57ce6d2cbfd31de1e3daf1bc978b5893152874c02f6d27b","license":"BUSL-1.1"},"contracts/interface/ICampaign.sol":{"content":"//SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.15;

import '@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol';
import { Consts } from '../Consts.sol';

interface ICampaign {
  //for voted: true = voted; false = not voted;
  //for choice: true = think cheat; false = think not cheat;
  struct Voter {
    bool voted;
    bool choice;
  }

  //for result: true = cheat; false = not cheat;
  //for state: true = over; false = working;
  //for legal: true = over 2/3; false = not enough voter;
  struct ChallengeRecord {
    uint256 challengerId;
    uint256 cheaterId;
    uint256 agreeCount;
    uint256 disagreeCount;
    uint256 challengeRiseTime;
    bool result;
    bool state;
    bool legal;
  }

  struct TokenProperty {
    TokenStatus tokenStatus;
    uint256 pendingReward;
  }

  enum TokenStatus {
    INVALID,
    EXIT,
    SIGNED,
    ADMITTED,
    ACHIEVED,
    FAILED,
    REKT
  }

  struct Record {
    string contentUri;
  }

  function initialize(
    address owner,
    IERC20Upgradeable token_,
    uint256 amount_,
    string memory name_,
    string memory symbol_,
    uint256 startTime_,
    uint256 totalPeriod_,
    uint256 periodLength_,
    string memory campaignUri_
  ) external;

  function status() external view returns (Consts.CampaignStatus);

  function totalEpochsCount() external view returns (uint256);

  function period() external view returns (uint256);

  function currentEpoch() external view returns (uint256);

  function getTokenProperties(uint256 tokenId) external view returns (TokenProperty memory);

  // owner update content uri
  function setCampaignUri(string calldata newUri) external;

  // user sign up the campaign
  function signUp() external;

  // host allow user to participate
  function admit(uint256[] calldata) external;

  // user check at a fixed frequency
  function checkIn(string calldata, uint256) external;

  // settle the reward
  function settle() external;

  // user claim reward after campaign ended
  function claim(uint256 tokenId) external;

  function claimAndWithdraw(uint256 tokenId) external;

  function withdraw() external;

  function challenge(uint256, uint256) external;

  function vote(
    uint256,
    uint256,
    bool
  ) external;

  function judgement(uint256) external;

  function forceEnd() external;

  // epoch update event;
  event EpochUpdated(uint256 currentEpoch);

  event EvCampaignUriSet(string newUri);

  event EvSignUp(uint256 tokenId);

  event EvRegisterSuccessfully(uint256 tokenId);

  event EvCheckIn(uint256 epoch, uint256 tokenId, string contentUri);

  event EvModifyRegistry(uint256[] tokenList, bool[] status);

  // settle the campaign event
  event EvSettle(address user);

  event EvClaimReward(uint256 tokenId, uint256 amount);
  // who fail
  event EvFailure(uint256 tokenId);
  // who success
  event EvSuccess(uint256 tokenId);

  event EvWithDraw(address host, uint256 hostReward, uint256 protocolFee);

  event EvChallenge(uint256 challengerId, uint256 cheaterId, uint256 challengeRecordId);

  event EvVote(uint256 tokenId, uint256 challengeRecordId);

  event EvJudgement(uint256 challengeRecordId);

  event EvCheat(uint256 cheaterId);
}
